mapper.select:
select count(1)
FROM t_income_materiel


    materiel_no LIKE concat('%',#{materielNo},'%')


    AND income_date >= #{startTime}


    AND income_date <= #{endTime}

SELECT a.id,a.materiel_no,a.income_num,a.income_date,b.materiel_name
FROM t_income_materiel a left join t_stock b on a.materiel_no=b.materiel_no


    and a.materiel_no LIKE concat('%',#{record.materielNo},'%')


    and b.materiel_name LIKE concat('%',#{record.materielName},'%')


      AND income_date >= #{record.startTime}


      AND income_date <= #{record.endTime}


ORDER BY income_date DESC
LIMIT #{page.index},#{page.pageSize}

