mapper.select:
select count(1)
FROM t_receive_materiel


    materiel_no LIKE concat('%',#{materielNo},'%')


    receiver LIKE concat('%',#{receiver},'%')


    AND receive_date >= #{startTime}


    AND receive_date <= #{endTime}

SELECT a.id,a.receiver,a.materiel_no,a.receive_num,a.receive_date,b.materiel_name
FROM t_receive_materiel a left join t_stock b ON a.materiel_no=b.materiel_no


    and a.materiel_no LIKE concat('%',#{record.materielNo},'%')


    and a.receiver LIKE concat('%',#{record.receiver},'%')


    and b.materiel_name LIKE concat('%',#{record.materielName},'%')


    AND a.receive_date >= #{record.startTime}


    AND a.receive_date <= #{record.endTime}


ORDER BY receive_date DESC
LIMIT #{page.index},#{page.pageSize}

